CREATE TABLE IF NOT EXISTS "finance_budgetcategory" ("id" integer NOT NULL PRIMARY KEY AUTOINCREMENT, "name" varchar(100) NOT NULL, "description" text NOT NULL, "allocation" decimal NOT NULL);
